try:
	Name = '_equipo_downtime'
	obj = system.mes.analysis.getMESAnalysisSettings(Name)
	end = system.date.parse(self.view.params.endDate, "yyyy-MM-dd HH:mm:ss")
	start = system.date.parse(self.view.params.startDate, "yyyy-MM-dd HH:mm:ss")
	params = {'eqPath' : self.session.custom.equipo}
	result = system.mes.analysis.executeAnalysis(start, end, obj, params)
	data = result.getDataset()
	pyData = system.dataset.toPyDataSet(data)
	pareto = []
	for j in range(data.rowCount):
		if data.getValueAt(j,1) !=0:
			pareto.append(data.getValueAt(j,1))
	porcentaje = sum(pareto)

	list = []
	for i in range(data.rowCount):
		if data.getValueAt(i,2) !=0:
			list.append( {'datetime': data.getValueAt(i,3), 'Razon': data.getValueAt(i,0), 'Tiempo': data.getValueAt(i,1), 'porcentaje': ((data.getValueAt(i,1))*100)/porcentaje} )
	return list
except:
	date = system.date.parse(self.view.params.startDate, "yyyy-MM-dd HH:mm:ss")
	return [{'datetime':date,'downtime':0}]

