messageHandlers.script:
# Check that the message is intended for this instance ID
if payload:
	payloadInstanceID = payload['instanceID'] if 'instanceID' in payload else -1

	if payloadInstanceID == self.view.params.instanceID:
			# Remove all datasources from the chart.
			self.props.dataSources = {}
			self.props.series = []

# Check that the message is intended for this instance ID
if payload:
	payloadInstanceID = payload['instanceID'] if 'instanceID' in payload else -1

	if payloadInstanceID == self.view.params.instanceID:

			# Goal: build a new datasources object along with matching series object.
			dataSources = {}
			series = []

			# Controlling parameters	
			dateStart = self.view.params.range.start
			dateEnd = self.view.params.range.end
			chartData = self.view.params.data
			modes = self.view.params.modes

			# If the modes list is undefined, gather data for all the modes of all the given process units.
			if not (len(modes) > 0):

				modes = []

				# Add the new modes to the modes list, only if they don't already exist.
				for unit in chartData:

					for event in chartData[unit]['events']:

						# Read each mode.
						newMode = event['mode'] if 'mode' in event else None			
						if newMode:

							uniqueFlag = True

							for existingMode in modes:
								if newMode == existingMode['name']:
									uniqueFlag = False
									break

							if uniqueFlag == True:
								modes.append(
									{
										'name': newMode,
										'color': ''
									}
								)		

			# For each mode, build a new series.
			for mode in modes:

				newSeriesName = mode['name'].strip() if 'name' in mode else ''
				newSeriesDatasource = mode['name'].replace(' ', '').replace('/', '').lower() if 'name' in mode else ''	
				newSeriesColor = mode['color']  if 'color' in mode else ''

				newSeries = self.buildSeries(newSeriesName, newSeriesDatasource, newSeriesColor, True)

				series.append(newSeries)

			# Build a series for the dedicated cursor.
			series.append(self.buildSeries('cursor', 'cursor', '', False))

			# For each series, gather the data.
			for s in series:	

				# Controlling parameters
				seriesMode = s['name']
				seriesEvents = []

				# Build the data for each event.
				for unit in chartData:

					unitSeriesEvents = []	
					for event in chartData[unit]['events']:

						eventMode = event['mode'] if 'mode' in event else None
						if eventMode == seriesMode:

							currentEvent = {
								'unit': chartData[unit]['name'] if 'name' in chartData[unit] else None,
								'start': event['start'] if 'start' in event else None,
								'end': event['end'] if 'end' in event else None
							}

							unitSeriesEvents.append(currentEvent)


					# If no series were created for a unit, put an empty data entry to enforce proper rendering.
					# This should not be required, but is needed because the property we need in the AMCharts Library isn't exposed by Ignition.			
					if not (len(unitSeriesEvents) > 0):
						currentEvent = {
							'unit': chartData[unit]['name'] if 'name' in chartData[unit] else None,
							'start': 0,
							'end': 1
						}		

						unitSeriesEvents.append(currentEvent)

					seriesEvents.extend(unitSeriesEvents)	

				# If the events exist for the series, create a datasource.
				if len(seriesEvents) > 0:
					dataSources[s['data']['source']] = seriesEvents			

			# Apply the new datasources/series to the chart.
			self.props.series = series
			self.props.dataSources = dataSources
