	# Get analysis result Data
	data = system.mes.analysis.executeAnalysis(start, end, obj, params).getDataset()
	# Maintenance events by row including the duration.
	if data.rowCount > 0:
		nOfRepairs = data.rowCount
		totalTime = 0.0
		for i in range(nOfRepairs):
			totalTime = totalTime + data.getValueAt(i,"Line State Duration")
		mttr = totalTime / float(nOfRepairs)
	else:
		mttr = 0
	# Convert to list
	return str(round(mttr, 1)) + " min"
except:
	return "000"
